mapper.select:
select 

from ludimb_vhlinfor vh LEFT JOIN ludimb_baseinfor base  on vh.vhiinforid=base.vhlinforid 
WHERE base.orderno=#{orderno,jdbcType=VARCHAR}

mapper.update:
update ludimb_vhlinfor set lcnNo = #{lcnno,jdbcType=VARCHAR}, engNo = #{engno,jdbcType=VARCHAR}, vinNo = #{vinno,jdbcType=VARCHAR}, brandCode = #{brandcode,jdbcType=VARCHAR}, vhlVal = #{vhlval,jdbcType=REAL}, vehicleName = #{vehiclename,jdbcType=VARCHAR}, carSort = #{carsort,jdbcType=INTEGER}, carStyle = #{carstyle,jdbcType=VARCHAR}, carMaker = #{carmaker,jdbcType=VARCHAR}, tonnage = #{tonnage,jdbcType=REAL}, setNo = #{setno,jdbcType=INTEGER}, displacement = #{displacement,jdbcType=REAL}, quality = #{quality,jdbcType=REAL}, marketYear = #{marketyear,jdbcType=DATE}, carReMark = #{carremark,jdbcType=VARCHAR}, riskName = #{riskname,jdbcType=VARCHAR}, fuelFlag = #{fuelflag,jdbcType=INTEGER}, newEnergyFlag = #{newenergyflag,jdbcType=INTEGER}, model = #{model,jdbcType=VARCHAR}, registerDate = #{registerdate,jdbcType=DATE}, chgownerFlag = #{chgownerflag,jdbcType=INTEGER}, transferDate = #{transferdate,jdbcType=DATE}, glassType = #{glasstype,jdbcType=VARCHAR}, certiFicateType = #{certificatetype,jdbcType=VARCHAR}, certiFicateNo = #{certificateno,jdbcType=VARCHAR}, certiFicateDate = #{certificatedate,jdbcType=DATE}, drvOwner = #{drvowner,jdbcType=VARCHAR}, certIType = #{certitype,jdbcType=VARCHAR}, certICode = #{certicode,jdbcType=VARCHAR}, owner_phoneNo = #{phoneno,jdbcType=INTEGER},
createDataTime = #{createdatatime,jdbcType=TIMESTAMP},
updateTime = #{updatetime,jdbcType=TIMESTAMP}, brandName = #{brandName,jdbcType=VARCHAR}, gearBoxType=#{gearboxtype,jdbcType=VARCHAR}, airBagFalg=#{airbagfalg,jdbcType=INTEGER}, alarmFlag=#{alarmflag,jdbcType=INTEGER}, fullWeight=#{fullweight,jdbcType=VARCHAR}, absFlag=#{absflag,jdbcType=VARCHAR}, familyKind=#{familyKind,jdbcType=VARCHAR}, codeName=#{codeName,jdbcType=VARCHAR} where vhiinforId = #{vhiinforid,jdbcType=INTEGER}
